import React from 'react'
import clsx from 'clsx'
import { twMerge } from 'tailwind-merge'

import { IComponentBaseProps, ComponentSize } from '../types'

import CardActions, { CardActionsProps as ActionProps } from './CardActions'
import CardBody, { CardBodyProps as BodyProps } from './CardBody'
import CardTitle, { CardTitleProps as TitleProps } from './CardTitle'
import CardImage, { CardImageProps as ImageProps } from './CardImage'

export type CardActionsProps = ActionProps
export type CardBodyProps = BodyProps
export type CardTitleProps = TitleProps
export type CardImageProps = ImageProps

export type CardProps = React.HTMLAttributes<HTMLDivElement> &
  IComponentBaseProps & {
    bordered?: boolean
    imageFull?: boolean

    // responsive props
    normal?: ComponentSize | boolean // Applies default paddings
    compact?: ComponentSize | boolean // Applies smaller padding
    side?: ComponentSize | boolean // The image in <figure> will be on to the side
  }

interface ModifierMap {
  [key: string]: {
    [key: string]: string | undefined
  }
}

const DYNAMIC_MODIFIERS: ModifierMap = {
  compact: {
    true: 'card-compact',
    xs: 'xs:card-compact',
    sm: 'sm:card-compact',
    md: 'md:card-compact',
    lg: 'lg:card-compact',
  },
  normal: {
    true: 'card-normal',
    xs: 'xs:card-normal',
    sm: 'sm:card-normal',
    md: 'md:card-normal',
    lg: 'lg:card-normal',
  },
  side: {
    true: 'card-side',
    xs: 'xs:card-side',
    sm: 'sm:card-side',
    md: 'md:card-side',
    lg: 'lg:card-side',
  },
}

const Card = React.forwardRef<HTMLDivElement, CardProps>(
  (
    { bordered = true, imageFull, normal, compact, side, className, ...props },
    ref
  ): JSX.Element => {
    const classes = twMerge(
      'card',
      className,
      clsx({
        'card-bordered': bordered,
        'image-full': imageFull,
        [(compact && DYNAMIC_MODIFIERS.compact[compact.toString()]) || '']:
          compact,
        [(normal && DYNAMIC_MODIFIERS.normal[normal.toString()]) || '']: normal,
        [(side && DYNAMIC_MODIFIERS.side[side.toString()]) || '']: side,
      })
    )

    return <div aria-label="Card" {...props} className={classes} ref={ref} />
  }
)

export default Object.assign(Card, {
  Actions: CardActions,
  Body: CardBody,
  Title: CardTitle,
  Image: CardImage,
})
import React, { cloneElement, LegacyRef } from 'react'
import clsx from 'clsx'
import { twMerge } from 'tailwind-merge'

import Button from '../Button'

export type CarouselItemWidth = 'full' | 'half'

export type CarouselItemProps = React.HTMLAttributes<HTMLDivElement> & {
  readonly innerRef?: LegacyRef<HTMLDivElement>
  src?: string
  alt?: string
  index?: number
  width?: CarouselItemWidth
  hasButtons?: boolean
  buttonStyle?: (value: string) => React.ReactElement
  onPrev?: () => void
  onNext?: () => void
}

const CarouselItem = ({
  children,
  innerRef,
  src,
  alt,
  index = 0,
  width,
  hasButtons,
  buttonStyle,
  onPrev,
  onNext,
  className,
  ...props
}: CarouselItemProps): JSX.Element => {
  const classes = twMerge(
    'carousel-item relative',
    className,
    clsx({
      'w-full': width === 'full',
      'w-1/2': width === 'half',
      'h-full': true,
    })
  )

  const imageClasses = clsx({
    'w-full': width === 'full',
  })

  const renderButtons = () => {
    if (buttonStyle != null) {
      return (
        <>
          {cloneElement(buttonStyle('❮'), {
            onClick: onPrev,
          })}
          {cloneElement(buttonStyle('❯'), {
            onClick: onNext,
          })}
        </>
      )
    }

    return (
      <>
        <Button onClick={onPrev} shape="circle">
          ❮
        </Button>
        <Button onClick={onNext} shape="circle">
          ❯
        </Button>
      </>
    )
  }

  return (
    <div {...props} id={`item${index}`} ref={innerRef} className={classes}>
      {src ? <img src={src} alt={alt} className={imageClasses} /> : children}
      {hasButtons && (
        <div className="absolute flex justify-between transform -translate-y-1/2 left-5 right-5 top-1/2">
          {renderButtons()}
        </div>
      )}
    </div>
  )
}

export default CarouselItem
import React, {
  cloneElement,
  createRef,
  forwardRef,
  ReactElement,
  RefObject,
  useEffect,
  useState,
} from 'react'
import clsx from 'clsx'
import { twMerge } from 'tailwind-merge'

import { IComponentBaseProps } from '../types'

import CarouselItem, {
  CarouselItemProps,
  CarouselItemWidth,
} from './CarouselItem'
import Button from '../Button'

export type CarouselProps = React.HTMLAttributes<HTMLDivElement> &
  IComponentBaseProps & {
    children: ReactElement<CarouselItemProps>[]
    display?: 'slider' | 'numbered' | 'sequential'
    snap?: 'start' | 'center' | 'end'
    vertical?: boolean
    width?: CarouselItemWidth
    buttonStyle?: (value: string) => React.ReactElement
  }

const Carousel = forwardRef<HTMLDivElement, CarouselProps>(
  (
    {
      children,
      display = 'slider',
      snap,
      vertical,
      width,
      buttonStyle,
      dataTheme,
      className,
      ...props
    },
    ref
  ): JSX.Element => {
    const classes = twMerge(
      'carousel',
      className,
      clsx({
        [`carousel-${snap}`]: snap,
        'carousel-vertical': vertical,
        'w-full': display !== 'slider',
      })
    )

    const [itemRefs, setItemRefs] = useState<RefObject<HTMLDivElement>[]>([])
    const [activeIndex, setActiveIndex] = useState(0)

    useEffect(() => {
      const newRefs: RefObject<HTMLDivElement>[] = []
      children.map((_) => {
        newRefs.push(createRef<HTMLDivElement>())
      })
      setItemRefs(newRefs)
    }, [children])

    const scrollToIndex = (index: number) => {
      itemRefs[index].current?.scrollIntoView({
        behavior: 'smooth',
        block: 'nearest',
        inline: snap,
      })
      setActiveIndex(index)
    }

    return (
      <>
        <div
          role="listbox"
          aria-label="Image carousel"
          {...props}
          ref={ref}
          data-theme={dataTheme}
          className={classes}
        >
          {children.map((child, i) => {
            return cloneElement(child, {
              innerRef: itemRefs[i],
              index: i + 1,
              children: child.props.children,
              src: child.props.src,
              alt: child.props.alt,
              width: display !== 'slider' ? 'full' : width,
              hasButtons: display === 'sequential',
              buttonStyle,
              onPrev: () =>
                scrollToIndex(i - 1 < 0 ? children.length - 1 : i - 1),
              onNext: () =>
                scrollToIndex(i + 1 > children.length - 1 ? 0 : i + 1),
              ...child.props,
            })
          })}
        </div>
        {display === 'numbered' && (
          <div className="flex justify-center w-full py-2 gap-2">
            {children.map((_, i) => {
              if (buttonStyle != null) {
                return cloneElement(buttonStyle((i + 1).toString()), {
                  key: i,
                  onClick: () => scrollToIndex(i),
                })
              }

              return (
                <Button
                  active={i === activeIndex}
                  key={i}
                  onClick={() => scrollToIndex(i)}
                >
                  {i + 1}
                </Button>
              )
            })}
          </div>
        )}
      </>
    )
  }
)

export default Object.assign(Carousel, { Item: CarouselItem })
